mapper.insert:
INSERT INTO fdcodegroup (
	fdcgName
	, fdcgDelNy
	, fdcgSeq
) VALUES (
	#{fdcgName}
	,0
)

mapper.update:
	    update 
	    	fdcodegroup 
   		SET
            fdcgName = #{fdcgName}
		WHERE 1=1
			and fdcgSeq = #{fdcgSeq}

