mapper.insert:
insert into fdrecipesave(
	fdrsName
	, fdrsIntroduction
	, fdrsServingsCd
	, fdrsTimeCd
	, fdrsDifficultyCd
	, fdrsTip
	, fdrsDelNy
	, regDateTime
	, modDateTime
) values (
	#{fdrsName}
	, #{fdrsIntroduction}
	, #{fdrsServingsCd}
	, #{fdrsTimeCd}
	, #{fdrsDifficultyCd}
	, #{fdrsTip}
	, 0
	, #{regDateTime}
	, #{modDateTime}
)

	SELECT last_insert_id()

INSERT INTO fdmaterial (
	fdmtMaterial
	, fdmtAmount
	, fdmtDelNy
	, fdrsSeq
) VALUES (
	#{fdmtMaterial}
	, #{fdmtAmount}
	, 0
	, #{fdrsSeq}
)

mapper.update:
UPDATE 
	fdmember
SET
	ifmmId = #{ifmmId}
	, ifmmPassword = #{ifmmPassword}
	, ifmmName = #{ifmmName}
	, ifmmGenderCd = #{ifmmGenderCd}
	, ifmmDob = #{ifmmDob}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE 
	fdmemberemail
SET
	fdmeEmailFull = #{fdmeEmailFull}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE 
	fdmemberphone
SET
	fdmpTelecomCd  = #{fdmpTelecomCd} 
	,fdmpNumber = #{fdmpNumber}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE 
	fdmemberaddress
SET
	fdmaDefaultNy = 1
	,fdmaZipcode = #{fdmaZipcode}
	,fdmaTitle1 = #{fdmaTitle1}
	,fdmaTitle2 = #{fdmaTitle2}
	,fdmaAddress = #{fdmaAddress}
	,fdmaDelNy = 0
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}
