mapper.select:
SELECT a.ifmmSeq , a.ifmmId , a.ifmmPassword , a.ifmmName , a.ifmmGenderCd , a.ifmmDob , a.ifmmDelNy , a.ifmmSaved , b.fdmeEmailFull , c.fdmpTelecomCd , c.fdmpNumber
, d.fdmaZipcode , d.fdmaTitle1 , d.fdmaTitle2 , d.fdmaAddress , a.ifmmEmailConsentNy , a.ifmmSmsConsentNy , a.regDateTime , a.modDateTime
LIMIT #{startRnumForMysql},#{rowNumToShow}

mapper.insert:
insert into fdMember(
	ifmmId
	, ifmmPassword
	, ifmmName
	, ifmmGenderCd
	, ifmmDob
	, ifmmDelNy
	, ifmmSaved
	, ifmmEmailConsentNy
	, ifmmSmsConsentNy
	, regDateTime
	, modDateTime
) values (
	#{ifmmId}
	, #{ifmmPassword}
	, #{ifmmName}
	, #{ifmmGenderCd}
	, #{ifmmDob}
	, 0
	, 1
	, #{ifmmEmailConsentNy}
	, #{ifmmSmsConsentNy}
	, #{regDateTime}
	, #{modDateTime}
)

	SELECT last_insert_id()

INSERT INTO fdmemberemail (
	fdmeEmailFull
	, fdmeDelNy
	, fdmeDefaultNy
	, ifmmSeq
) VALUES (
	#{fdmeEmailFull}
	,0
	,1
	,#{ifmmSeq}
)

INSERT INTO fdmemberphone (
	fdmpNumber
	, fdmpTelecomCd 
	, fdmpDelNy
	, fdmpDefaultNy
	, ifmmSeq
) VALUES (
	#{fdmpNumber}
	,#{fdmpTelecomCd} 
	,0
	,1
	,#{ifmmSeq}
)

INSERT INTO fdmemberaddress (
	fdmaZipcode
	, fdmaTitle1 
	, fdmaTitle2
	, fdmaAddress
	, fdmaDelNy
	, fdmaDefaultNy
	, ifmmSeq
) VALUES (
	#{fdmaZipcode}
	,#{fdmaTitle1}
	,#{fdmaTitle2}
	,#{fdmaAddress} 
	,0
	,1
	,#{ifmmSeq}
)

mapper.update:
UPDATE 
	fdmember
SET
	ifmmId = #{ifmmId}
	, ifmmPassword = #{ifmmPassword}
	, ifmmName = #{ifmmName}
	, ifmmGenderCd = #{ifmmGenderCd}
	, ifmmDob = #{ifmmDob}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE 
	fdmemberemail
SET
	fdmeEmailFull = #{fdmeEmailFull}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE 
	fdmemberphone
SET
	fdmpTelecomCd  = #{fdmpTelecomCd} 
	,fdmpNumber = #{fdmpNumber}
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}

UPDATE 
	fdmemberAddress
SET
	fdmaDefaultNy = 1
	fdmaZipcode = #{fdmaZipcode}
	fdmaTitle1 = #{fdmaTitle1}
	fdmaTitle2 = #{fdmaTitle2}
	fdmaAddress = #{fdmaAddress}
	fdmaDelNy = 0
WHERE 1=1
	AND ifmmSeq = #{ifmmSeq}
